    private void sendRequestWithOkHttp(String test) {



        MediaType type = MediaType.parse("application/json;charset=utf-8");
        RequestBody RequestBody2 = RequestBody.create(type, test);
        try {
            OkHttpClient client = new OkHttpClient();
            Request request = new Request.Builder()
                    // 指定访问的服务器地址
                    .url("http://3714w993m5.qicp.vip/sendData")
                    .post(RequestBody2)
                    .build();
            Response response = client.newCall(request).execute();
            System.out.println("11111112222222222222");
            Headers headers = response.headers();
            System.out.println(headers);

            String s = response.body().toString();
            System.out.println(s);
            String responseData = response.body().string();
            parseJSONWithJSONObject(responseData);
            System.out.println(responseData);
        } catch (Exception e) {
            System.out.println("1111111");
            e.printStackTrace();
        }
    }


    private void parseJSONWithJSONObject(String jsonData) {
        try {
            JSONObject object = new JSONObject(jsonData);
            String name = object.getString("RESULT");
            //日志
            Log.d("name", "结果是：" + name);

        } catch (JSONException e) {
            e.printStackTrace();
        }
    }
